package kdata.iftest;

public class IfTest2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a, b;
		a = 10;
		b = 20;
		
		if(a > b)
			System.out.println(a + "이(가) 더 큽니다");
		else if(a < b)
			System.out.println(b + "이(가) 더 큽니다");
		else
			System.out.println("같습니다");
	}

}
